mapper.update:
UPDATE
    member
SET
    name = #{name}
,   phone = #{phone}
,   birth = #{birth}
,   email = #{email}
,   update_date = NOW()
WHERE
    member_no = #{memberNo}

UPDATE
    store
SET
    profile = #{profile}
,   store_name = #{storeName}
,   store_info = #{storeInfo}
WHERE
    store_id = #{storeId}
